            return `Guess is correct`
        }
        else if (guess >= number)
        {  return `Guess is High`}
        else if (guess <= number)
        {  return `Guess is low`}

    }
    const [phone, setPhone] = useState("");
    return (
      <div>
          <h1>Enter your number </h1>
          <input placeholder = "Guess the number" onChange={(event) => {setPhone(event.target.value)}}></input>
       <button onClick={checkNumber()}>Click</button>
      </div>
    );
  }

