import React, { useState } from "react";

function GuessNumber(number) {
    const [guess, setGuess] = useState("");
    const [guessText, setGuessText] = useState("");
    const checkNumber = (guess)=> { 
        if (guess === number)
        {
            setGuessText(`Guess is correct`)
        }
        else if (guess >= number)
        { setGuessText(`Guess is High`)}
        else if (guess <= number)
        {  setGuessText(`Guess is low`)}

    }

    return (
      <div>
          <h1>Enter your number </h1>
          <input placeholder = "Guess the number" onChange={(event) => {setGuess(event.target.value)}}></input>
       <button onClick={checkNumber(guess)}>Click</button>
       <h1></h1>
      </div>
    );
  }

  export default GuessNumber;
